+        .device-section {
+            flex: 1;
+            display: flex;
+            align-items: center;
+            justify-content: center;
+            width: 100%;
+            position: relative;
+            padding: 20px 0 40px 0;
+            perspective: 1000px;
+            overflow: hidden;
+            min-height: 500px;
+            mask-image: linear-gradient(to top, 
+                rgba(0,0,0,1) 0%, 
+                rgba(0,0,0,1) 85%, 
+                rgba(0,0,0,0.8) 92%, 
+                rgba(0,0,0,0) 100%);
+            -webkit-mask-image: linear-gradient(to top, 
+                rgba(0,0,0,1) 0%, 
+                rgba(0,0,0,1) 85%, 
+                rgba(0,0,0,0.8) 92%, 
+                rgba(0,0,0,0) 100%);
+        }
+
+        .device-preview {
+            position: relative;
+            width: 100%;
+            height: 100%;
+            min-height: 400px;
+        }
+
+        /* Concentric Circles Background */
+        .concentric-circles {
+            position: absolute;
+            top: 30%;
+            left: 50%;
+            transform: translate(-50%, -50%);
+            width: 150vw;
+            height: 150vw;
+            max-width: 1400px;
+            max-height: 1400px;
+            pointer-events: none;
+            z-index: 0;
+            animation: circleFloat 6s ease-in-out infinite;
+        }
+
+        @keyframes circleFloat {
+            0%, 100% { transform: translate(-50%, -50%) translateY(0); }
+            25% { transform: translate(-50%, -50%) translateY(-20px); }
+            75% { transform: translate(-50%, -50%) translateY(10px); }
+        }
+
+        .circle {
+            position: absolute;
+            top: 50%;
+            left: 50%;
+            transform: translate(-50%, -50%);
+            border-radius: 50%;
+            border: 1px solid;
+            animation: pulse 4s ease-out infinite;
+        }
+
+        .circle:nth-child(1) {
+            width: 20%;
+            height: 20%;
+            min-width: 200px;
+            min-height: 200px;
+            border-color: rgba(134, 134, 139, 0.3);
+            animation-delay: 0s;
+        }
+
+        .circle:nth-child(2) {
+            width: 35%;
+            height: 35%;
+            min-width: 300px;
+            min-height: 300px;
+            border-color: rgba(134, 134, 139, 0.25);
+            animation-delay: 0.5s;
+        }
+
+        .circle:nth-child(3) {
+            width: 50%;
+            height: 50%;
+            min-width: 400px;
+            min-height: 400px;
+            border-color: rgba(134, 134, 139, 0.2);
+            animation-delay: 1s;
+        }
+
+        .circle:nth-child(4) {
+            width: 65%;
+            height: 65%;
+            min-width: 500px;
+            min-height: 500px;
+            border-color: rgba(134, 134, 139, 0.15);
+            animation-delay: 1.5s;
+        }
+
+        .circle:nth-child(5) {
+            width: 80%;
+            height: 80%;
+            min-width: 600px;
+            min-height: 600px;
+            border-color: rgba(134, 134, 139, 0.1);
+            animation-delay: 2s;
+        }
+
+        @keyframes pulse {
+            0% {
+                opacity: 0;
+                transform: translate(-50%, -50%) scale(0.8);
+            }
+            50% {
+                opacity: 1;
+            }
+            100% {
+                opacity: 0;
+                transform: translate(-50%, -50%) scale(1.2);
+            }
+        }
+
+        /* Ecosystem Device Silhouettes */
+        .ecosystem-device {
+            position: absolute;
+            z-index: 0;
+            opacity: 0.6; /* added transparency */
+        }
+
+        /* iPhone Silhouette */
+        .iphone-silhouette {
+            left: -50px;
+            top: 30%;
+            transform: translateY(-50%) rotate(-8deg);
+            width: 140px;
+            height: 280px;
+            background: #c4c4c9;
+            border-radius: 24px;
+            box-shadow:
+                inset 0 0 0 10px #d1d1d6,
+                inset 0 0 0 12px #c4c4c9;
+        }
+
+        .iphone-silhouette::before {
+            content: '';
+            position: absolute;
+            top: 18px;
+            left: 50%;
+            transform: translateX(-50%);
+            width: 50px;
+            height: 3px;
+            background: #d1d1d6;
+            border-radius: 2px;
+        }
+
+        .iphone-silhouette::after {
+            content: '';
+            position: absolute;
+            top: 35px;
+            left: 18px;
+            right: 18px;
+            bottom: 35px;
+            background: var(--screen-color);
+            border-radius: 4px;
+            opacity: 1; /* fully opaque */
+        }
+
+        /* Laptop Silhouette */
+        .laptop-silhouette {
+            right: -160px;
+            top: 30%;
+            transform: translateY(-50%) rotate(8deg);
+            width: 320px;
+            height: 200px;
+        }
+
+        .laptop-screen {
+            width: 100%;
+            height: 180px;
+            background: #c4c4c9;
+            border-radius: 10px;
+            position: relative;
+            box-shadow: inset 0 0 0 10px #d1d1d6;
+        }
+
+        .laptop-screen::after {
+            content: '';
+            position: absolute;
+            top: 18px;
+            left: 18px;
+            right: 18px;
+            bottom: 18px;
+            background: var(--screen-color);
+            border-radius: 4px;
+            opacity: 1; /* fully opaque */
+        }
+
+        .laptop-base {
+            width: 120%;
+            height: 10px;
+            background: #c4c4c9;
+            margin: -2px auto 0;
+            border-radius: 0 0 6px 6px;
+            position: relative;
+            left: -10%;
+        }
+
+        .laptop-base::before {
+            content: '';
+            position: absolute;
+            top: 2px;
+            left: 50%;
+            transform: translateX(-50%);
+            width: 50px;
+            height: 3px;
+            background: #d1d1d6;
+            border-radius: 1px;
+        }
+
+        /* Override inherited opacity so screens stay solid */
+        .ecosystem-device { opacity: 0.6 !important; }
+
+        /* Ticker Tape Styles */
+        .ticker-container {
+            position: absolute;
+            width: 100%;
+            overflow: hidden;
+            opacity: 0.6;
+        }
+
+        .ticker-bottom {
+            bottom: 10%;
+        }
+
+        .ticker-content {
+            display: flex;
+            white-space: nowrap;
+            animation: scroll 30s linear infinite;
+        }
+
+        .ticker-bottom .ticker-content {
+            animation: scrollReverse 30s linear infinite;
+        }
+
+        @keyframes scroll {
+            0% { transform: translateX(0); }
+            100% { transform: translateX(-50%); }
+        }
+
+        @keyframes scrollReverse {
+            0% { transform: translateX(-50%); }
+            100% { transform: translateX(0); }
+        }
+
+        .ticker-item {
+            padding: 0 40px;
+            font-size: 16px;
+            color: var(--text-secondary);
+            font-family: 'SF Mono', Monaco, monospace;
+            display: inline-flex;
+            align-items: center;
+            gap: 10px;
+        }
+
+        .ticker-item::before {
+            content: "•";
+            color: var(--sociail-yellow);
+            font-weight: bold;
+        }
+
+        .ticker-item.work {
+            color: var(--sociail-blue);
+        }
+
+        .ticker-item.personal {
+            color: #d4a017;
+        }
+
+        .ticker-item.family {
+            color: #34c759;
+        }
+
+        .ticker-item.creative {
+            color: #ff3b30;
+        }
+
+        /* 3D Device Styles */
+        .device-float {
+            position: absolute;
+            top: 30%;
+            left: 50%;
+            transform: translate(-50%, -50%);
+            width: 320px;
+            height: 120px;
+            animation: float 6s ease-in-out infinite;
+        }
+
+        @keyframes float {
+            0%, 100% { transform: translate(-50%, -50%) translateY(0) rotateX(0deg) rotateY(0deg); }
+            25% { transform: translate(-50%, -50%) translateY(-20px) rotateX(5deg) rotateY(15deg); }
+            75% { transform: translate(-50%, -50%) translateY(10px) rotateX(-5deg) rotateY(-15deg); }
+        }
+
+        /* AI Speech Visualization Animation */
+        @keyframes aiSpeaking {
+            0%, 30%, 100% { opacity: 1; }
+            /* "So" syllable - stronger, longer */
+            40% { opacity: 0.3; }
+            40.2% { opacity: 0.8; }
+            40.4% { opacity: 0.4; }
+            40.6% { opacity: 0.9; }
+            40.8% { opacity: 0.5; }
+            41% { opacity: 1; }
+            41.2% { opacity: 0.6; }
+            41.4% { opacity: 0.9; }
+            41.6% { opacity: 0.7; }
+            41.8% { opacity: 1; }
+            /* Brief pause between syllables */
+            42%, 42.5% { opacity: 1; }
+            /* "cial" syllable - softer, quicker */
+            42.6% { opacity: 0.5; }
+            42.7% { opacity: 0.8; }
+            42.8% { opacity: 0.6; }
+            42.9% { opacity: 0.9; }
+            43% { opacity: 0.4; }
+            43.1% { opacity: 0.7; }
+            43.2% { opacity: 0.5; }
+            43.3% { opacity: 0.8; }
+            43.4% { opacity: 0.6; }
+            43.5% { opacity: 1; }
+            /* Natural pause before "Assistant" */
+            43.6%, 45% { opacity: 1; }
+            /* "A" syllable */
+            45.1% { opacity: 0.4; }
+            45.2% { opacity: 0.7; }
+            45.3% { opacity: 0.5; }
+            45.4% { opacity: 0.9; }
+            45.5% { opacity: 1; }
+            /* "ssis" syllable */
+            45.6% { opacity: 0.5; }
+            45.7% { opacity: 0.8; }
+            45.8% { opacity: 0.4; }
+            45.9% { opacity: 0.7; }
+            46% { opacity: 0.6; }
+            46.1% { opacity: 0.9; }
+            46.2% { opacity: 0.5; }
+            46.3% { opacity: 0.8; }
+            46.4% { opacity: 1; }
+            /* "tant" syllable */
+            46.5% { opacity: 0.6; }
+            46.6% { opacity: 0.8; }
+            46.7% { opacity: 0.4; }
+            46.8% { opacity: 0.7; }
+            46.9% { opacity: 0.5; }
+            47% { opacity: 0.9; }
+            47.1% { opacity: 0.6; }
+            47.2% { opacity: 1; }
+            /* Normal until next burst */
+            47.3%, 70% { opacity: 1; }
+            /* Second pronunciation - slightly different pattern */
+            75% { opacity: 0.4; }
+            75.2% { opacity: 0.9; }
+            75.4% { opacity: 0.3; }
+            75.6% { opacity: 0.8; }
+            75.8% { opacity: 0.5; }
+            76% { opacity: 1; }
+            76.2% { opacity: 0.7; }
+            76.4% { opacity: 0.9; }
+            /* Pause */
+            76.6%, 77% { opacity: 1; }
+            /* "cial" */
+            77.1% { opacity: 0.6; }
+            77.2% { opacity: 0.8; }
+            77.3% { opacity: 0.4; }
+            77.4% { opacity: 0.7; }
+            77.5% { opacity: 0.5; }
+            77.6% { opacity: 0.9; }
+            77.7% { opacity: 0.6; }
+            77.8% { opacity: 1; }
+            /* Pause before "Assistant" */
+            77.9%, 79% { opacity: 1; }
+            /* "Assistant" - compressed */
+            79.1% { opacity: 0.5; }
+            79.2% { opacity: 0.8; }
+            79.3% { opacity: 0.6; }
