/* Navigation Styles */
nav {
    position: fixed;
    top: 0;
    left: 0;
    right: 0;
    background: rgba(251, 251, 253, 0.8);
    backdrop-filter: saturate(180%) blur(20px);
    z-index: 1000;
    border-bottom: 1px solid var(--border-color);
    transition: all 0.3s ease;
}

@media (prefers-color-scheme: dark) {
    nav {
        background: rgba(0, 0, 0, 0.8);
    }
}

[data-theme="dark"] nav {
    background: rgba(0, 0, 0, 0.8);
}

.nav-content {
    max-width: 980px;
    margin: 0 auto;
    padding: 0 22px;
    height: 44px;
    display: flex;
    align-items: center;
    justify-content: space-between;
}

.logo {
    display: flex;
    align-items: center;
    gap: 8px;
    text-decoration: none;
    color: var(--text-primary);
    font-weight: 500;
}

.logo-badge {
    background: var(--sociail-yellow);
    padding: 4px 12px;
    border-radius: 20px;
    font-weight: 600;
    letter-spacing: 0.5px;
}

.logo-badge span {
    background: var(--sociail-blue);
    color: white;
    padding: 2px 4px;
    border-radius: 4px;
    margin: 0 1px;
    font-size: 0.9em;
    letter-spacing: inherit;
}

.nav-links {
    display: flex;
    gap: 30px;
    list-style: none;
}

.nav-links a {
    color: var(--text-primary);
    text-decoration: none;
    font-size: 14px;
    opacity: 0.8;
    transition: opacity 0.3s;
}

.nav-links a:hover {
    opacity: 1;
}

.theme-toggle {
    display: flex;
    align-items: center;
    gap: 15px;
}

.theme-switch {
    background: none;
    border: none;
    cursor: pointer;
    padding: 6px;
    border-radius: 6px;
    transition: background-color 0.2s ease;
    display: flex;
    align-items: center;
    justify-content: center;
}

.theme-switch:hover {
    background: rgba(0, 0, 0, 0.05);
}

@media (prefers-color-scheme: dark) {
    .theme-switch:hover {
        background: rgba(255, 255, 255, 0.1);
    }
}

[data-theme="dark"] .theme-switch:hover {
    background: rgba(255, 255, 255, 0.1);
}

.theme-icon {
    width: 18px;
    height: 18px;
    color: var(--text-primary);
    opacity: 0.8;
    transition: opacity 0.2s ease;
}

.theme-switch:hover .theme-icon {
    opacity: 1;
}
